using EcommerceApi.Models;
using EcommerceApi.ViewModel;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace EcommerceApi.Untilities
{
    public class CustomerStatementTemplateGenerator : ICustomerStatementTemplateGenerator
    {
        private readonly EcommerceContext _context;

        public CustomerStatementTemplateGenerator(EcommerceContext context)
        {
            _context = context;
        }

        public string GetHtmlString(
            CustomerViewModel customer, 
            IEnumerable<CustomerPaidOrdersViewModel> customerPaidOrders,
            IEnumerable<CustomerUnPaidOrdersViewModel> customerUnPaidOrders,
            DateTime fromDate,
            DateTime toDate)
        {
            var sbFinal = new StringBuilder();
            var posSetting = _context.PortalSettings.FirstOrDefault();
            // temp solution
            var imageSrc = "<img src='data:image/png;base64,' />";
            if (posSetting.LegalName.Contains("Veroboard", System.StringComparison.InvariantCultureIgnoreCase))
            {
                imageSrc = "<img src='data:image/png;base64,' /> ";
            }

            sbFinal.Append($@"
                        <html>
                            <head>
                            </head>
                            <body>
                                <div class='center'>
                                   {imageSrc}
                                </div>
                                <div class='center xsmall-font'>{posSetting.LegalName}</div>
                                <div class='center xsmall-font'>{posSetting.InvoicePhone}</div>
                                <div class='center xsmall-font spaceafter-10'>{posSetting.InvoiceWebsite}</div>
                                <hr/>
                                <div>{posSetting.LegalName}</div>
                                <div class='xsmall-font spaceafter-10'>");

            foreach (var location in _context.Location.Where(l => !l.Disabled && l.ShowOnInvoice)) {
                sbFinal.Append($@"<b>{location.LocationName}:</b> <br /> 
                                     {location.LocationAddress}, <br />
                                     {location.LocationName}, {location.Province} {location.PostalCode} <br />
                                     Phone: {location.PhoneNumber} <br />
                                ");
            }

                                sbFinal.Append($@"</div><hr />
                                <table style='width:100%'>
<tr>
  <td style='width:33%'><td style='width:33%;text-align:center'><h3>Monthly Statement</h3></td><td style='width:33%'></td>
</tr>
<tr>
  <td style='width:33%'>
      Date: {toDate.ToShortDateString()} <br/>
      Report Period: {fromDate.ToShortDateString()} - {toDate.ToShortDateString()}
  </td>
  <td style='width:33%'></td>
  <td style='width:33%'>
     Customer: {customer.CompanyName} <br/>
     Account Number: {customer.CustomerCode} <br/>
     Address: {customer.Address} <br/>
     {customer.City}, {customer.Province} <br/>
     {customer.PostalCode}
  </td>
</tr>
</table>
<br/>

<hr/>
<h3>Paid</h3>
<table>
<tr>
<th style='border: 1px solid black; padding:5px'>Invoice No</td>
<th style='border: 1px solid black; padding:5px'>PO Number</td>
<th style='border: 1px solid black; padding:5px'>Total Sale</td>
<th style='border: 1px solid black; padding:5px'>Status</td>
<th style='border: 1px solid black; padding:5px'>Date</td>
<th style='border: 1px solid black; padding:5px'>Payment Amount</td>
<th style='border: 1px solid black; padding:5px'>Paid By</td>
</tr>
{GetPaidAmountRows(customerPaidOrders)}
</table>

<br/>

<h3>Awaiting Payment - Till Date</h3>
<table style='border-collapse: collapse;'>
<tr>
<th style='border: 1px solid black; padding:5px'>Invoice No</td>
<th style='border: 1px solid black; padding:5px'>PO Number</td>
<th style='border: 1px solid black; padding:5px'>Total Sale</td>
<th style='border: 1px solid black; padding:5px'>Status</td>
<th style='border: 1px solid black; padding:5px'>Date</td>
<th style='border: 1px solid black; padding:5px'>Due Date</td>
</tr>
{GetUnPaidAmountRows(customerUnPaidOrders)}
</table>

<p>
<b>
  <span style='fond-size:20px;'>To pay by cheque for an invoice, please
        <span style='fond-size:30px;'>PAY TO THE ORDER OF: PIXEL PRINT LTD.</span>
  </span>
</b>
</p>

<hr/>

<p>
The payments are pending for awaiting payment transactions, if any. Please pay the amount to avoid late fee of 2% on total per transaction.
</p>
<p>
Please contact Customer Service at 1-888-672-2627 with any questions and comments.
</p>

<h4>
Thanks you for your business.
</h4>
");

            sbFinal.Append("</body></ html>");

            return sbFinal.ToString();
        }

        private static string GetPaidAmountRows(IEnumerable<CustomerPaidOrdersViewModel> customerPaidOrders)
        {
            var tableSb = new StringBuilder();
            foreach (var item in customerPaidOrders)
            {
                tableSb.Append($@"<tr>
                    <td style='border: 1px solid black; padding:5px'>{item.OrderId}</td>
                    <td style='border: 1px solid black; padding:5px'>{item.PoNumber}</td>
                    <td style='border: 1px solid black; padding:5px'>${item.Total}</td>
                    <td style='border: 1px solid black; padding:5px'>{item.Status}</td>
                    <td style='border: 1px solid black; padding:5px'>{item.OrderDate.ToShortDateString()}</td>
                    <td style='border: 1px solid black; padding:5px'>${item.PaymentAmount}</td>
                    <td style='border: 1px solid black; padding:5px'>{item.PaymentTypeName}</td>
                    <tr>");
            }
            return tableSb.ToString();
        }

        private static string GetUnPaidAmountRows(IEnumerable<CustomerUnPaidOrdersViewModel> customerUnPaidOrders)
        {
            var tableSb = new StringBuilder();
            var total = customerUnPaidOrders.Sum(s => s.Total);
            foreach (var item in customerUnPaidOrders)
            {
                tableSb.Append($@"<tr>
                    <td style='border: 1px solid black; padding:5px'>{item.OrderId}</td>
                    <td style='border: 1px solid black; padding:5px'>{item.PoNumber}</td>
                    <td style='border: 1px solid black; padding:5px'>${item.Total}</td>
                    <td style='border: 1px solid black; padding:5px'>{item.Status}</td>
                    <td style='border: 1px solid black; padding:5px'>{item.OrderDate.ToShortDateString()}</td>
                    <td style='border: 1px solid black; padding:5px'>{item.DueDate.ToShortDateString()}</td>
                    <tr>");
            }
            tableSb.Append($@"<tr>
                    <td style='border: 1px solid black; padding:5px'></td>
                    <td style='border: 1px solid black; padding:5px'>Total: </td>
                    <td style='border: 1px solid black; padding:5px'>${total}</td>
                    <td style='border: 1px solid black; padding:5px'></td>
                    <td style='border: 1px solid black; padding:5px'></td>
                    <td style='border: 1px solid black; padding:5px'></td>
                    <tr>");

            return tableSb.ToString();
        }
    }

    public interface ICustomerStatementTemplateGenerator
    {
        string GetHtmlString(
            CustomerViewModel customer,
            IEnumerable<CustomerPaidOrdersViewModel> customerPaidOrders,
            IEnumerable<CustomerUnPaidOrdersViewModel> customerUnPaidOrders,
            DateTime fromDate,
            DateTime toDate);
    }
}
